<? if (@$images) { ?>  <div id="carousel-example-generic" class="carousel slide" data-ride="carousel">    <!-- Indicators -->    <ol class="carousel-indicators">      <? $cnt_c = 0;?>      <? foreach ($images as $image) { ?>        <li <?=($cnt_c == 0) ? 'class="active"' : '';?> data-target="#carousel-example-generic" data-slide-to="<?=$cnt_c++;?>" ></li>      <? } ?>    </ol>    <!-- Wrapper for slides -->    <div class="carousel-inner">      <? $cnt = 0;?>      <? foreach ($images as $image) { ?>        <div class="item<?=($cnt++ == 0) ? ' active' : '';?>">          <img src="<?=$image['image'];?>" alt="<?=$image['params']['description_'. $this->language];?>">          <div class="carousel-caption">            <?=$image['params']['description_'. $this->language];?>          </div>        </div>      <? } ?>    </div>    <!-- Controls -->    <a class="left carousel-control" href="#carousel-example-generic" data-slide="prev">      <span class="glyphicon glyphicon-chevron-left"></span>    </a>    <a class="right carousel-control" href="#carousel-example-generic" data-slide="next">      <span class="glyphicon glyphicon-chevron-right"></span>    </a>  </div><? } ?>